import React from "react";
import {
    LineChart,
    ResponsiveContainer,
    Legend, Tooltip,
    Line,
    XAxis,
    YAxis,
    CartesianGrid
} from 'recharts';
function Chart({ pTime, pCases, pDeaths, pTests}) {
    const pdata = chart.map( (chart, index )=> (<Chart key={index} pTime={chart.time}  pCases={chart.cases.total} pDeaths={chart.deaths.total}  pTests={chart.tests.total} />))

    return (
        <div>
        <ResponsiveContainer width="100%" aspect={3}>
            <LineChart  margin={{ right: 300 }}>
                <CartesianGrid />
                <XAxis dataKey={pTime}
                    interval={'preserveStartEnd'} />
                <YAxis></YAxis>
                <Legend />
                <Tooltip />
                <Line dataKey={pCases}
                    stroke="blue" activeDot={{ r: 8 }} />
                <Line dataKey={pDeaths}
                    stroke="green" activeDot={{ r: 8 }} />
                <Line dataKey={pTests}
                    stroke="green" activeDot={{ r: 8 }} />
            </LineChart>
        </ResponsiveContainer>
    </div>
        );
    }

    export default Chart;
