import React, {useEffect, useState} from "react";
 import {
    LineChart,
    ResponsiveContainer,
    Legend, Tooltip,
    Line,
    XAxis,
    YAxis,
    CartesianGrid
} from 'recharts';

function ChartContainer({ pTime, pCases, pDeaths, pTests}) {
  const [chart, setChart] = useState([]);

  const options = {
    method: 'GET',
    headers: {
        'X-RapidAPI-Key': '05b4a1e1d0msh7eefee18e7eb6e6p150558jsn3c0c2db9d4ee',
        'X-RapidAPI-Host': 'covid-193.p.rapidapi.com'
    }
};
  useEffect(() => {
    fetch(`https://covid-193.p.rapidapi.com/history?country=usa&day=2020-06-02`, options)
            .then((res) => res.json())
            .then((chart) => {
                console.log(chart.response)
                setChart(chart.response);
            });
        }, []);
        const pdata = chart.map( (chart, index)=> (<Chart key={index} pTime={chart.time}  pCases={chart.cases.total} pDeaths={chart.deaths.total}  pTests={chart.tests.total} />))


        return (
          <div className="container-fluid">
           <ResponsiveContainer width="100%" aspect={3}>
            <LineChart data={pdata} margin={{ right: 300 }}>
                <CartesianGrid />
                <XAxis dataKey={pTime}
                    interval={'preserveStartEnd'} />
                <YAxis></YAxis>
                <Legend />
                <Tooltip />
                <Line dataKey={pCases}
                    stroke="blue" activeDot={{ r: 8 }} />
                <Line dataKey={pDeaths}
                    stroke="green" activeDot={{ r: 8 }} />
                <Line dataKey={pTests}
                    stroke="green" activeDot={{ r: 8 }} />
            </LineChart>
        </ResponsiveContainer>
          </div>
        );
      }
      export default ChartContainer;
